import React, { useState, useEffect } from 'react';
import { ControleEditora } from "./Controle/ControleEditora";

export default function LivroLista() {
    let [livros, setLivros] = UseState([]);
    let [carregado, setCarregado] = useState(false);

    useEffect(() => {
        if (!carregado) {
            setLivros(controleLivro.obterLivros());
            setCarregado(true);
        }
    }, [carregado]);

    let excluir = (codigo) => {
        controleLivro.excluir(codigo);
        setCarregado(false);
    };

    return (
        <div>
            <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
                <a className="navbar-brand" href="#">Biblioteca</a>
                <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNavAltMarkup" aria-controls="navbarNavAltMarkup" aria-expanded="false" aria-label="Toggle navigation">
                    <span className="navbar-toggler-icon"></span>
                </button>
                <div className="collapse navbar-collapse" id="navbarNavAltMarkup">
                    <div className="navbar-nav">
                        <a className="nav-item nav-link active" href="#">Livros <span className="sr-only">(current)</span></a>
                        <a className="nav-item nav-link" href="#">Editoras</a>
                    </div>
                </div>
            </nav>
            <main className="container">
                <h1 className="my-4">Livros</h1>
                <table className="table table-striped">
                    <thead>
                        <tr>
                            <th scope="col">Título</th>
                            <th scope="col">Editora</th>
                            <th scope="col">Autores</th>
                            <th scope="col">Ação</th>
                        </tr>
                    </thead>
                    <tbody>
                        {livros.map((livro) => 
                            <LinhaLivro key={livro.codigo} livro={livro} excluir={excluir} />
                        )}
                    </tbody>
                </table>
            </main>
        </div>
    );
}

function LinhaLivro(props) {
    let livro = props.livro;
    let nomeEditora = ControleEditora.getNomeEditora(livro.codEditora);

    return (
        <tr>
            <td>{livro.titulo}</td>
            <td>{nomeEditora}</td>
            <td>{livro.autores.join(', ')}</td>
            <td><button className="btn btn-danger" onClick={() => props.excluir(livro.codigo)}>Excluir</button></td>
        </tr>
    );
}
