import { Livro } from "../Modelo/Livros";   
let livros: Livro[] = [

    new Livro(1,1, 'Livro A', 'Resumo A', ['Autor A']),

    new Livro(2,2, 'Livro B', 'Resumo B', ['Autor B']),
    new Livro(3,3, 'Livro C', 'Resumo C', ['Autor C']),
];

export class ControleLivros {
    obterLivros(){
        return livros;
    }

    incluir(livro: Livro){
        let codigoMaximo = Math.max(...livros.map(livro => livro.codigo));
        livro.codigo = codigoMaximo + 1;
        livros.push(livro);
    }

    excluir(codigo: number) {
        let indice = livros.findIndex(livro => livro.codigo === codigo);
        if (indice >= 0) {
            livros.splice(indice, 1);
        }
    }
}
