import React, { useState, useEffect } from 'react';
import { ControleLivros } from './Controle/ControleLivros'; 
import { ControleEditora } from './Controle/ControleEditora';
import { Navbar, Nav } from 'react-bootstrap';

let controleLivro = new ControleLivros();
let controleEditora = new ControleEditora();

function LinhaLivro(props) {
    let livro = props.livro;
    let nomeEditora = controleEditora.getNomeEditora(livro.codEditora);

    return (
        <tr>
            <td>{livro.título}
            <button className="btn btn-danger float-start m-2" onClick={() => props.excluir(livro.codigo)}>Excluir</button></td>
            <td>{livro.resumo}</td>
            <td>{nomeEditora}</td>
            <td>{livro.autores.join(', ')}</td>
            <td></td>
        </tr>
    );
}

export default function LivroLista() {
    let [livros, setLivros] = useState([]);
    let [carregado, setCarregado] = useState(false);

    useEffect(() => {
        if (!carregado) {
            setLivros(controleLivro.obterLivros());
            setCarregado(true);
        }
    }, [carregado]);

    let excluir = (codigo) => {
        controleLivro.excluir(codigo);
        setCarregado(false);
    };

    return (
        <div>
            {/* <Navbar bg="dark" variant="dark" expand="lg">
                <Navbar.Brand href="#">Biblioteca</Navbar.Brand>
                <Navbar.Toggle aria-controls="basic-navbar-nav" />
                <Navbar.Collapse id="basic-navbar-nav">
                    <Nav className="mr-auto"> 
                        <Nav.Link href="#">Livros</Nav.Link> 
                        <Nav.Link href="#">Editoras</Nav.Link>
                    </Nav>
                </Navbar.Collapse>
            </Navbar> */}
            <main className="container">
                <h1 className="my-4">Livros</h1>
                <table className="table table-striped">
                    <thead>
                        <tr>
                            <th className='bg-dark text-white' scope="col">Título</th>
                            <th scope="col">Resumo</th>
                            <th scope="col">Editora</th>
                            <th scope="col">Autores</th>
                        </tr>
                    </thead>
                    <tbody>
                        {livros.map((livro) => 
                            <LinhaLivro key={livro.codigo} livro={livro} excluir={excluir} />
                        )}
                    </tbody>
                </table>
            </main>
        </div>
    );
}
